import { Box, Typography } from '@mui/material';
import React, { Fragment } from 'react';
import { Controller, useForm } from "react-hook-form";
import Select from "react-select";

const StudentsAdmission = () => {

    const { register, control, handleSubmit, ormState: { errors } } = useForm({
        defaultValues: {
            firstName: '',
            select: {}
        }
    });
    const onSubmit = data => console.log(data);


    return (
        <Fragment>
            <Box className="main-page">
                <Box className="container-fluid">
                    <div className="page-title-div">
                        <Typography className="title text-left" variant="h5" gutterBottom >Student Admission </Typography>
                    </div>
                </Box>
                <Box className=''>
                </Box>
                <Box>
                    <Box className='student_admission_from_box'>
                        <form onSubmit={handleSubmit(onSubmit)}>
                           <Box className='dp-flex'> 
                             <Typography>Student Name</Typography>
                             <Box className='dp-grid'>
                             <input
                                {...register("firstName", { required: true })}

                            />
                            {/* {errors.firstName?.type === 'required' && <p role="alert">First name is required</p>} */}
                            </Box>
                           </Box>
                           <Box className='dp-flex'> 
                             <Typography>Student Email</Typography>
                             <Box className='dp-grid'>
                             <input
                                {...register("mail", { required: "Email Address is required" })} />

                            </Box>
                           </Box> 
                            <Controller
                                name="select"
                                control={control}
                                render={({ field }) => <Select
                                    {...field}
                                    options={[
                                        { value: "chocolate", label: "Chocolate" },
                                        { value: "strawberry", label: "Strawberry" },
                                        { value: "vanilla", label: "Vanilla" }
                                    ]}
                                />}
                            />
                            <input type="submit" />
                        </form>
                    </Box>
                </Box>
            </Box>
        </Fragment>
    );
};

export default StudentsAdmission;
